.quotes {
	display: grid;
	grid-row-gap: 40px;
}

.quotation-mark-icon {
	display: flex;
	align-items: center;
	justify-content: center;
}
.quotes {
	.slider {
		position: relative;
		display: grid;
    	grid-row-gap: 40px;
	}

	.slide-text {
		text-align: center;
	}

	.slides {
		width: 100%;
    	max-width: 620px;
		position: relative;
		margin: 0 auto;
		overflow: hidden;
		height: 220px;
		@media(max-width: 700px) {
			height: 500px;
		}
	}

	.slide {
		position: absolute;
		height: 100%;
		width: 100%;
		transition: all .25s;
		transform: translateX(-200%);
	}

	.slide.next + .slide {
		transform: translateX(200%);
	}

	.slide.prev {
		z-index: 10;
		transform: translateX(-100%);
	}
	.slide.current {
		z-index: 10;
		transform: translateX(0);
	}
	.slide.next {
		z-index: 10;
		transform: translateX(100%);
	}

	.slide {
		display: none;
		z-index: -1;

		&.current, &.next, &.prev {
			display: grid;
			align-content: center;
			justify-content: center;
		}

		blockquote {
			margin: 0;
		}
	}

	.controls {
		position: absolute;
		display: flex;
		align-items: center;
		justify-content: space-between;
		width: 100%;
		top: 50%;

		@media(max-width: $break-tablet) {
			top: unset;
			bottom: 0;
		}
		
		.goToPrev, .goToNext {
			outline: unset;
			padding: 0;
			margin: 0;
			background: unset;
			border: none;

			cursor: pointer;
			transition: all 0.35s linear;
			
			&:hover {
				transform: scale(1.5);
                g g {
					fill: var(--orange);
					stroke: var(--orange);
                }
            }
		}
		.goToPrev {
			padding: 10px 10px 10px 0;
		}
		.goToNext {
			padding: 10px 0 10px 10px;
		}
	}

	.dots {
		display: flex;
		align-items: center;
		justify-content: center;
		margin: 0;
		list-style: none;
		padding: 0;

		.dot {
			height: 3px;
			width: 15px;
			margin: 0 2px;

			&.current-dot {
				background-color: var(--black);
			}
			&:not(.current-dot) {
				background-color: var(--grey);
			}
		}
	}
}

