.wrapper {
    max-width: 1440px;
    margin: 0 auto;

    display: grid;
    grid-gap: 60px 15px;
    grid-template-columns: 1fr minmax(300px, 1180px) 1fr;

    @media (min-width: 900px) {
        grid-gap: 130px 15px;
    }

    & > * {
        grid-column: 2;
    }

    & .full-bleed {
        width: 100%;
        grid-column: 1 / -1;

        @media(max-width: $break-desktop) {
            grid-column: 2;
        }
    }
}
