  margin: 15px 0 40px 0;
}
.blog-posts .post-meta .author {
  margin: 0;
}
.blog-posts [aria-labelledby=insights] .category {
  color: var(--orange);
}
.blog-posts [aria-labelledby=news] .category {
  color: var(--purple);
}
.blog-posts [aria-labelledby=events] .category {
  color: var(--blue);
}
