  .questions .faq-text {
    max-width: 380px;
  }
}
.questions .faq-text h2 {
  margin: 0 0 30px 0;
  font-size: 28px;
  line-height: 36px;
  font-weight: 700;
}
.questions .faq-text p {
  font-size: 16px;
  line-height: 30px;
  font-weight: 400;
  color: var(--darkGrey);
}
.questions .faqs {
  padding: 25px 0;
  position: relative;
}
.questions .faqs:first-of-type {
  padding: 0 0 25px 0;
}
.questions .faqs:after {
  content: "";
  height: 1px;
  background: linear-gradient(to right, var(--orange) 50%, var(--offWhite) 50%);
  background-size: 200% 1px;
  background-position: right bottom;
  transition: all 0.5s ease-out;
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
}
.questions .faqs:hover:after {
  background-position: left bottom;
}
.questions .question {
  outline: none;
  background: unset;
  border: none;
  padding: 0;
  margin: 0;
  display: flex;
  justify-content: space-between;
  width: 100%;
  font-size: 16px;
  line-height: 30px;
  font-weight: 600;
  cursor: pointer;
  color: var(--black);
  text-align: left;
}
.questions .question:hover {
  color: var(--orange);
}
.questions .question[aria-selected=false]:after {
  content: url("/6d256905a0d77fb1cd1806644440cd52.svg");
  height: 35px;
  width: 35px;
}
.questions .question[aria-selected=false] + .answer {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-in;
}
.questions .question[aria-selected=true]:after {
  content: url("/e35bbc0c2d388f62fa70bf2f04fb09b9.svg");
  height: 35px;
  width: 35px;
}
.questions .question[aria-selected=true] + .answer {
  max-height: 200px;
  transition: max-height 0.3s ease-out;
}
.questions .answer p {
  font-size: 16px;
  line-height: 30px;
  font-weight: 400;
  color: var(--darkGrey);
  /* margin: 30px 0 40px 0; */
  margin: 0;
}

.features {
  display: grid;
