.features .features-container {
  display: grid;
  grid-gap: 45px 35px;
  grid-template-columns: repeat(auto-fit, minmax(360px, 1fr));
}
.features .feature {
  text-align: left;
}
.features .feature .feature-icon {
  width: 55px;
