  display: flex;
  align-items: center;
  justify-content: unset;
}
.sectors button {
  height: 45px;
  width: 45px;
  background-color: var(--blueGrey);
  outline: none;
  border: none;
  padding: 0;
  margin: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
