  border-radius: 3px 0 0 3px;
}
.sectors button.goToPrev:hover #Artboard {
  fill: var(--orange);
}
.sectors button.goToNext {
  border-radius: 0 3px 3px 0;
}
.sectors button.goToNext:hover #Artboard {
  fill: var(--orange);
}
.sectors .slider {
  background-color: var(--offWhite);
}
