  display: grid;
  grid-gap: 3px;
  grid-auto-flow: column;
  grid-template-columns: repeat(auto-fit, minmax(380px, 1fr));
  transform: translate(130px, -50px);
}
.sectors .slider figure {
  min-width: 380px;
}
.sectors .slider a {
  text-decoration: none;
}
.sectors .slider a h3 {
  padding: 0 0 0 10px;
  margin: 30px 0 0 0;
}
.sectors .slider a:hover h3 {
  color: var(--orange);
}
.sectors .slides {
  position: relative;
  min-height: 380px;
}
.sectors .slide {
  transition: all 0.25s;
  transform: translateX(200%);
  position: absolute;
  width: 380px;
  height: 100%;
}
.sectors .slide.current, .sectors .slide.next, .sectors .slide.prev, .sectors .slide.next + .slide {
  display: grid;
  z-index: 10;
}
.sectors .slide.next + .slide {
  transform: translateX(200%);
}
.sectors .slide.prev {
  transform: translateX(300%);
}
.sectors .slide.current {
  transform: translateX(0);
}
.sectors .slide.next {
  transform: translateX(100%);
}

