        border: none;
        padding: 0;
        margin: 0;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;

        #Artboard {
            fill: var(--white);
            stroke: transparent;
        }
        &.goToPrev {
            border-radius: 3px 0 0 3px;
            &:hover {
                #Artboard {
                    fill: var(--orange);
                }
            }
        }
        &.goToNext {
            border-radius: 0 3px 3px 0;

            &:hover {
                #Artboard {
                    fill: var(--orange);
                }
            }
        }
    }

    .slider {
        background-color: var(--offWhite);

        .slides {
            display: grid;
            grid-gap: 3px;
            grid-auto-flow: column;
            grid-template-columns: repeat(auto-fit, minmax(380px, 1fr));
            transform: translate(130px, -50px);
