        a:hover {
            h3 {
                color: var(--orange);
            }
        }
    }

	.slides {
        position: relative;
        min-height: 380px;
	}

	.slide {
        transition: all 0.25s;
        transform: translateX(200%);
        position: absolute;
        width: 380px;
        height: 100%;

        // display: none;

        &.current, &.next, &.prev, &.next + .slide {
            display: grid;
            z-index: 10;
        }
	}

	.slide.next + .slide {
        transform: translateX(200%);
    }

	.slide.prev {
        transform: translateX(300%);
    }
    
	.slide.current {
        transform: translateX(0);
    }
    
	.slide.next {
        transform: translateX(100%);
	}

}

